//LTrim(string):È¥³ı×ó±ßµÄ¿Õ¸ñ
function LTrim(str){
	if (sstr == null || sstr == undefined) {
		return sstr;
	}
    var whitespace = new String("\t\n\r");
    var s = new String(str);

    if (whitespace.indexOf(s.charAt(0)) != -1)
    {
        var j = 0, i = s.length;
        while (j < i && whitespace.indexOf(s.charAt(j)) != -1)
        {
            j++;
        }
        s = s.substring(j, i);
    }
    return s;
}


//RTrim(string):È¥³ıÓÒ±ßµÄ¿Õ¸ñ
function RTrim(str){
	if (sstr == null || sstr == undefined) {
		return sstr;
	}
    var whitespace = new String("\t\n\r");
    var s = new String(str);
    if (whitespace.indexOf(s.charAt(s.length - 1)) != -1)
    {
        var i = s.length - 1;
        while (i >= 0 && whitespace.indexOf(s.charAt(i)) != -1)
        {
            i--;
        }
        s = s.substring(0, i + 1);
    }
    return s;
}


function comdecimal(thestr){
    var dolft = parseFloat(thestr);
    thestr = new String(dolft);

    var dotlst = thestr.indexOf(".");
    if (dotlst == 0) {
        thestr = "0" + thestr;
    }

    var dot2nd = thestr.lastIndexOf(".");
    //ÒÔÏÂÇé¿öÔòÉèÖÃÎª0.00
    if ((thestr == 0.00) || (thestr == 0) || (thestr == '')) {
        return "0.00";
    }
    //Èç¹ûÊÇÕûÊıÔòÉèÖÃ
    if (dotlst == -1) {
        return thestr + ".00";
    }
    //ËùÌîÊı¾İ²»Õı³££¬ÖØĞÂÖÃÁã
    if (dotlst != dot2nd) {
        return "0.00";
    }
    var intpart = thestr.substring(0, dotlst);
    var decpart = thestr.substring(dot2nd + 1);
    if (decpart.length > 2) {
        //Ğ¡ÊıµãºóµÚÈıÎ»Óë5Ïà±È½Ï£¬´óÓÚ½øÎ»£¬·ñÔò²»½øÎ»¡£
        var decpart3 = decpart.substring(2, 3);
        decpart = decpart.substring(0, 2);
        if (parseInt(decpart3) >= 5) {
            if (decpart.substring(0, 1) == 0) {
                decpart = parseInt(decpart.substring(1, 2)) + 1;
            } else {
                decpart = parseInt(decpart) + 1;
            }
        }
        decpart = decpart.toString();
        if (decpart.length == 1) {
            decpart = "0" + decpart;
        } else {
            if (decpart.length == 3) {
                decpart = "00";
                intpart = Math.round(parseFloat(intpart) + 1);
            }

        }
        var thestr = intpart + "." + decpart;


    } else {
        if (decpart.length == 1) {
            decpart = decpart + "0";
            var thestr = intpart + "." + decpart;
            //OK!
        }

    }
    return thestr;

}


