    /**
    * »ñÈ¡¸ø¶¨ÈÕÆÚºóÏà¼ä¸ôµÄÈÕÆÚ
    *
    * @dateString   ¸ø¶¨µÄÈÕÆÚ×Ö´®(±ØĞëÊÇDate.parse()¿ÉÒÔ½âÎöµÄÀàĞÍ)      ¿ÉÒÔÎªnull,Èç¹ûÎªnullÔò±íÊ¾È¡µ±Ç°ÈÕÆÚ
    * @interval     Ê±¼ä¼ä¸ô(Õı¸º¶¼¿ÉÒÔ)   ²»Îª¿Õ»ònull
    * @dateField    È¡Öµ1¡¢2¡¢3¡¢4¡¢5¡¢6£¬ÆäÖĞ1´ú±íyear¡¢2´ú±ímonth¡¢3´ú±íday¡¢4´ú±íhours¡¢5´ú±íminutes¡¢6´ú±íseconds  ²»Îª¿Õ»ònull
    */
    function getDate(dateString,interval,dateField){
        var tempDate;
        //È¡µ±Ç°ÈÕÆÚ
        if(dateString==null){
           tempDate=new Date();
        } else {
           tempDate=new Date(dateString);
        }

        switch(dateField){
            case 1:
                tempDate.setFullYear(tempDate.getFullYear()+interval);
                break;
            case 2:
                tempDate.setMonth(tempDate.getMonth()+interval);
                break;
            case 3:
                tempDate.setDate(tempDate.getDate()+interval);
                break;
            case 4:
                tempDate.setHours(tempDate.getHours()+interval);
                break;
            case 5:
                tempDate.setMinutes(tempDate.getMinutes()+interval);
                break;
            case 6:
                tempDate.setSeconds(tempDate.getSeconds()+interval);
                break;
        }
        //alert("ÈÕÆÚÎª="+tempDate.toGMTString());
        return tempDate;
    }


