function getValueFromSelectOptions(obj) {
	var length = obj.length;
	if (length == 1) {
	    return obj.options[0].text;	    	    	    	
	} else {
		for (var i = 0; i < length; i ++) {
			if (obj.options[i].value == obj.value) {
				return obj.options[i].text;	 
			}
		}
	} 
	return null;    
}

