 function matchBracket(str) {
	var leftB = 0;
	for (var i = 0; i < str.length; i++)
	{
		if (str.charAt(i) == '(')
		{
			leftB++;
		}
		if (str.charAt(i) == ')')
		{
			leftB--;
		}
	}
	//alert(str + " " + leftB);
	return leftB == 0 ? true : false;
 } 

