//timeFlag : true,·µ»ØÄêÔÂÈÕÊ±·ÖÃë£¬false£¬Ö»·µ»ØÄêÔÂÈÕ
function getNow(timeFlag){ 
	var now = new Date(); 
	var yy = now.getFullYear(); 
	var mm = now.getMonth(); 
	var dd = now.getDate();
	var hour=now.getHours() < 10 ? "0"+now.getHours() : now.getHours();
	var minute=now.getMinutes() < 10 ? "0"+now.getMinutes() : now.getMinutes();
	var second=now.getSeconds() < 10 ? "0"+now.getSeconds() : now.getSeconds();
	var mmm=new Array();
	mmm[0]="01";
	mmm[1]="02";
	mmm[2]="03";
	mmm[3]="04";
	mmm[4]="05";
	mmm[5]="06";
	mmm[6]="07";
	mmm[7]="08";
	mmm[8]="09";
	mmm[9]="10";
	mmm[10]="11";
	mmm[11]="12";
	mm=mmm[mm];
	dd = dd < 10 ? ("0" + dd) : (""+dd);
	var time = yy + "-" + mm +"-"+dd ;
	if (timeFlag) {
		time += " " + hour + ":" + minute
	}
	return time;
}

